import React from "react";
import { gql, useQuery } from "@apollo/client"

const query_epidodes = gql`
query{
    episodesByIds(ids: [1, 2]) {
      name
      characters {
        name
      }
    }
  }`

function Episodes(){
    const { loading, data } = useQuery(query_epidodes)

    if(loading) return <div><p>Carregando... 0_0</p></div>

    return(
    <div>
        {
           data.episodesByIds.map((ob) => {
               <p>{ob.name}</p>
           })
        }
        {console.log(data.episodesByIds.name)}
    </div>
    )
}

export default Episodes
